import React from 'react'

function TodoList(props) {
    const {todos, setTodos} = props
    //   setTodos([...todos,setTodos])
    const deleteTodo= (id)=>{
        const updateDelete = todos.filter(todos => todos.id !== id )
        setTodos(updateDelete)
    }

    const Completed = (id)=>{
        const completeTodo = todos.map(todo=>{
            if (todo.id === id){
                todo.isCompleted = true;
                return todo;
            }
        setTodos(completeTodo)
        })
    }
    return (
        <div class="d-flex justify-content-center">
           <div class="list-group col-md-5 d-flex justify-content-center">
                <button
                    type="button"
                    class="list-group-item list-group-item-action active"
                    aria-current="true"
                >
                    TODO LIST
                </button>
                {
                    todos.map(tasklist=>{
                     return(

                <button type="button" class="list-group-item list-group-item-action d-flex justify-content-between">

                     {tasklist.isCompleted ? <input type="checkbox" checked /> : <input type="checkbox" /> }


                        <span onClick={()=>Completed(tasklist.id)}>
                            {tasklist.task}
                        </span>
                <i class="fas fa-trash" onClick={()=> deleteTodo(tasklist.id)}></i>
                </button>
                     )
                    })
                }





           </div>
        </div>
    )
}

export default TodoList
